mapper.select:
SELECT c.id, u.avatar, u.nickname, r.nickname AS reply_nickname, a.article_title, c.comment_content, c.create_time, c.is_delete FROM comment c LEFT JOIN article a ON c.article_id = a.id LEFT JOIN user_info u ON c.user_id = u.id LEFT JOIN user_info r ON c.reply_id = r.id
c.is_delete = #{condition.isDeleted}
and u.nickname like concat('%',#{condition.keywords},'%')
ORDER BY create_time DESC LIMIT #{condition.current},#{condition.size}

SELECT count(1) from comment c LEFT JOIN user_info u ON c.user_id = u.id
c.is_delete = #{condition.isDeleted}
and u.nickname like concat('%',#{condition.keywords},'%')
